REPLACE INTO FLIGHTSTATS_AIRPORT (SELECT AIRPORT_D, COUNT(DISTANCE) AS LEGS, SUM(IF((ATTR & 0x10) > 0, 1, 0)) AS ACARS, 
SUM(IF((ATTR & 2) > 0, 1, 0)) AS VATSIM, SUM(IF((ATTR & 4) > 0, 1, 0)) AS IVAO, SUM(IF((ATTR & 0x200) > 0, 1, 0)) AS HIST, SUM(IF((ATTR & 0x4000) > 0, 1, 0)) AS DSP,
SUM(DISTANCE) AS MILES, SUM(FLIGHT_TIME) AS HOURS, COUNT(DISTINCT PILOT_ID) AS PIDS, AVG(LOADFACTOR), SUM(PAX), SUM(IF(FSVERSION=2000,1,0)) AS FS7,
SUM(IF(FSVERSION=2002,1,0)) AS FS8, SUM(IF(FSVERSION=2004,1,0)) AS FS9, SUM(IF(FSVERSION=2006,1,0)) AS FSX, SUM(IF(FSVERSION=2008,1,0)) AS P3D,
SUM(IF(FSVERSION=2017,1,0)) AS P3Dv4, SUM(IF(FSVERSION=100,1,IF(FSVERSION=101,1,0))) AS XP, SUM(IF(FSVERSION=102,1,0)) AS XP11, SUM(IF(FSVERSION=2020,1,0)) AS FS2020,
SUM(IF(FSVERSION=0,1,0)) AS FSO, 1 FROM PIREPS WHERE (STATUS=3) GROUP BY AIRPORT_D);

REPLACE INTO FLIGHTSTATS_AIRPORT (SELECT AIRPORT_A, COUNT(DISTANCE) AS LEGS, SUM(IF((ATTR & 0x10) > 0, 1, 0)) AS ACARS, 
SUM(IF((ATTR & 2) > 0, 1, 0)) AS VATSIM, SUM(IF((ATTR & 4) > 0, 1, 0)) AS IVAO, SUM(IF((ATTR & 0x200) > 0, 1, 0)) AS HIST, SUM(IF((ATTR & 0x4000) > 0, 1, 0)) AS DSP,
SUM(DISTANCE) AS MILES, SUM(FLIGHT_TIME) AS HOURS, COUNT(DISTINCT PILOT_ID) AS PIDS, AVG(LOADFACTOR), SUM(PAX), SUM(IF(FSVERSION=2000,1,0)) AS FS7,
SUM(IF(FSVERSION=2002,1,0)) AS FS8, SUM(IF(FSVERSION=2004,1,0)) AS FS9, SUM(IF(FSVERSION=2006,1,0)) AS FSX, SUM(IF(FSVERSION=2008,1,0)) AS P3D,
SUM(IF(FSVERSION=2017,1,0)) AS P3Dv4, SUM(IF(FSVERSION=100,1,IF(FSVERSION=101,1,0))) AS XP, SUM(IF(FSVERSION=102,1,0)) AS XP11, SUM(IF(FSVERSION=2020,1,0)) AS FS2020,
SUM(IF(FSVERSION=0,1,0)) AS FSO, 0 FROM PIREPS WHERE (STATUS=3) GROUP BY AIRPORT_A);

REPLACE INTO FLIGHTSTATS_PILOT (SELECT PILOT_ID, COUNT(DISTANCE) AS LEGS, SUM(IF((ATTR & 0x10) > 0, 1, 0)) AS ACARS, 
SUM(IF((ATTR & 2) > 0, 1, 0)) AS VATSIM, SUM(IF((ATTR & 4) > 0, 1, 0)) AS IVAO, SUM(IF((ATTR & 0x200) > 0, 1, 0)) AS HIST, SUM(IF((ATTR & 0x4000) > 0, 1, 0)) AS DSP,
SUM(DISTANCE) AS MILES, SUM(FLIGHT_TIME) AS HOURS, COUNT(DISTINCT PILOT_ID) AS PIDS, AVG(LOADFACTOR), SUM(PAX), SUM(IF(FSVERSION=2000,1,0)) AS FS7,
SUM(IF(FSVERSION=2002,1,0)) AS FS8, SUM(IF(FSVERSION=2004,1,0)) AS FS9, SUM(IF(FSVERSION=2006,1,0)) AS FSX, SUM(IF(FSVERSION=2008,1,0)) AS P3D,
SUM(IF(FSVERSION=2017,1,0)) AS P3Dv4, SUM(IF(FSVERSION=100,1,IF(FSVERSION=101,1,0))) AS XP, SUM(IF(FSVERSION=102,1,0)) AS XP11, SUM(IF(FSVERSION=2020,1,0)) AS FS2020,
SUM(IF(FSVERSION=0,1,0)) AS FSO FROM PIREPS WHERE (STATUS=3) GROUP BY PILOT_ID);

